create OR REPLACE FUNCTION shelf()
RETURNS VOID AS $$
DECLARE
closet_id UUID;
BEGIN
FOR closet_id IN (SELECT DISTINCT id FROM closet)
                     LOOP
                         FOR i IN 1..10
                     LOOP
                     INSERT INTO shelf (id, created_date, is_active, updated_date, closet_id, count_of_book, row_number)
                     VALUES
                     ( gen_random_uuid(), now(), true, now(), closet_id, 0, i);
END LOOP;
END LOOP;
END;
                     $$
LANGUAGE plpgsql;

